    @GetMapping("/t")
    public Authentication whoAmI() {
        SecurityContext context = SecurityContextHolder.getContext();
        JwtAuthenticationToken jwtAuthenticationToken = (JwtAuthenticationToken) context.getAuthentication();
        Jwt jwt = jwtAuthenticationToken.getToken();
        String userId = jwt.getSubject();
        System.out.println(userId);
        System.out.println(jwtAuthenticationToken.getAuthorities().contains(new SimpleGrantedAuthority("ROLE_player")));
        return context.getAuthentication();
    }
    @GetMapping("/admin")
    @PreAuthorize("hasRole('admin')")
    public String getName() {
        SecurityContext context = SecurityContextHolder.getContext();
        return context.getAuthentication().getName();
    }

    @GetMapping("/player")
    @PreAuthorize("hasAnyRole('player', 'admin')")
    public ResponseEntity<String> getPlayerText(){
        return ResponseEntity.of(Optional.of("ma player halo!!!"));
    }


